mapper.select:
select b.article_no, b.user_id, b.subject, b.content, b.hit, b.register_time, m.user_name, b.bcode
from board b join users m
on b.user_id = m.user_id
where b.bcode = #{bcode}

order by b.article_no desc
limit #{start}, #{listsize}

select count(article_no)
from board
where bcode = #{bcode}

mapper.update:
update board
set hit = hit + 1
where article_no = #{articleNo}
