mapper.select:
select count(article_no)
from board
where bcode = #{bcode}

mapper.update:
update reboard
set step = step + 1
where group_no = #{groupNo} and step > #{step}

update reboard
set reply = reply + 1
where article_no = #{patendNo}

update board
set hit = hit + 1
where article_no = #{articleNo}
